"use client";
import { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";

const testimonialsData = [
  { id: 1, text: "This is the first testimonial", author: "John Doe" },
  { id: 2, text: "This is the second testimonial", author: "Jane Smith" },
  { id: 3, text: "This is the third testimonial", author: "David Johnson" },
];

export default function TestimonialCarousel() {
  const [index, setIndex] = useState(0);

  useEffect(() => {
    const timer = setInterval(() => {
      setIndex((prev) => (prev + 1) % testimonialsData.length);
    }, 3000); // change every 3s
    return () => clearInterval(timer);
  }, []);

  return (
    <div className="relative w-full max-w-lg mx-auto overflow-hidden h-40 flex items-center justify-center">
      <AnimatePresence mode="wait">
        <motion.div
          key={testimonialsData[index].id}
          initial={{ x: "100%", opacity: 0 }}
          animate={{ x: 0, opacity: 1 }}
          exit={{ x: "-100%", opacity: 0 }}
          transition={{ duration: 0.6, ease: "easeInOut" }}
          className="absolute w-full text-center p-4 bg-white shadow-lg rounded-lg"
        >
          <p className="text-lg font-medium italic text-gray-700">
            "{testimonialsData[index].text}"
          </p>
          <span className="block mt-2 text-sm font-semibold text-gray-500">
            - {testimonialsData[index].author}
          </span>
        </motion.div>
      </AnimatePresence>
    </div>
  );
}
