import React, { useState, useEffect } from 'react';

// This is where all our other components will be rendered.
const App = () => {
  const [isDarkMode, setIsDarkMode] = useState(true);

  // Function to toggle dark mode
  const toggleDarkMode = () => {
    setIsDarkMode(!isDarkMode);
  };

  // useEffect to add/remove the 'dark' class from the document's root element
  useEffect(() => {
    if (isDarkMode) {
      document.documentElement.classList.add('dark');
    } else {
      document.documentElement.classList.remove('dark');
    }
  }, [isDarkMode]);

  return (
    <div className="bg-gray-50 dark:bg-gray-900 text-gray-900 dark:text-gray-100 min-h-screen font-sans transition-colors duration-500">
      {/* Our components will go here */}
    </div>
  );
};

export default App;
