// ✅ ExperienceItem.jsx
import { Briefcase } from "lucide-react";

const ExperienceItem = ({ title, company, duration, points, index }) => {
  const isEven = index % 2 === 0;

  return (
    <div className="relative flex flex-col md:flex-row items-center md:items-start">
      {/* Icon on timeline */}
      <div className="hidden md:flex absolute left-1/2 transform -translate-x-1/2 top-6 z-10">
        <div className="w-10 h-10 flex items-center justify-center rounded-full bg-teal-500 border-4 border-slate-800 shadow-lg">
          <Briefcase className="h-5 w-5 text-white" />
        </div>
      </div>

      {/* Content */}
      <div
        className={`md:w-5/12 ${isEven ? "md:ml-auto" : "md:mr-auto"} relative`}
      >
        <div className="bg-zinc-800/50 p-6 rounded-lg border border-slate-700 shadow-md">
          <span className="bg-teal-400 text-slate-900 text-xs font-bold px-3 py-1 rounded-full absolute -top-3">
            {duration}
          </span>
          <h3 className="font-bold text-2xl mb-1">{title}</h3>
          <p className="text-sm font-medium text-teal-400 mb-4">{company}</p>
          <ul className="text-sm text-gray-400 space-y-2">
            {points.map((point, i) => (
              <li key={i}>• {point}</li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default ExperienceItem;
